def nb_handles (handles)
        nb = 0
    handles.each {|handle| nb+=1}
    puts "Il y a #{nb} handles dans cette array.\n"

end

def court_handle(handles)
  taille = handles[0].length
  court = handles[0]
  handles.each do |handle|
    if handle.length < taille
      taille = handle.length
      court = handle
    end
  end
  puts "Le handle de plus court est #{court}."
end

def cinq_chars(handles)
  count = 0
  handles.each do |handle|
    if handle.length == 6
      count += 1
    end
  end
  puts "Il y a #{count} handles qui contiennent 5 caractères"
end

def commence_maj(handles)
  count = 0

  handles.each do |handle|
    if handle[1].ord >= 65 && handle[1].ord <=90
      count +=1
    end
  end
  puts "Il y a #{count} handles qui commencent par une majuscule"
  
end

def sort_alphabet(handles)

  handles.sort_by! { |mot| mot.downcase }
  puts "\n==== Handles triées dans l'ordre alphabétique. ===="
  
  rep = " "
  
  until rep == "o" || rep == "n" do
    puts "\nVeux tu afficher la liste triée alphabétiquement? O/N"
    print "> "
    rep = gets.chomp

    if rep == "O" || rep == "N"
      rep = rep.downcase
    end    
  end

  if rep == "o"
    print handles
  end
puts

end

def sort_length(handles)
  handles.sort_by! { |mot| mot.length} 
  puts "==== Handles triées par longueurs. ================"
  rep = " "
  
  until rep == "o" || rep == "n" do
    puts "\nVeux tu afficher la liste triée par taille ? O/N"
    print "> "
    rep = gets.chomp

    if rep == "O" || rep == "N"
      rep = rep.downcase
    end    
  end

  if rep == "o"
    print handles
  end
puts
end

def epenser(handles)

  position = 0
  handles.each do |handle|
    if handle == "@epenser"
      position
      break
    end
    position+=1
  end
  puts "@epenser se trouve à la position n° #{position+1}"

end

def repartition(handles)
  rep = []
  handles.each do |handle|
    if rep[handle.length-1] == nil
      rep[handle.length-1]=1
    else rep[handle.length-1] += 1
    end
    
  end
  
  counter = 0

  rep.each do |handle|
    if(handle != nil)
      puts "Mots de #{counter} lettres : #{handle}"
    end
    counter += 1
      
  end

end

def menu (handles)

  rep = " "

    while rep != "q"
    rep = "q"
